            text_h = $(cl).data("text-hover");
            icon_h = $(cl).data("icon-hover");
            border_h = $(cl).data("border-hover");

            bg = ( 'undefined' !== typeof bg ) ? 'background-color:'+bg+' !important;' : '';
            text = ( 'undefined' !== typeof text ) ? 'color:'+text+' !important;' : '';
